import React from 'react';

function StudentForm({ student, handleInputChange, handleSubmit, buttonLabel }) {
    return (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <label>Name:</label>
                <input
                    type="text"
                    className="form-control"
                    name="name"
                    value={student.name}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <div className="form-group">
                <label>Age:</label>
                <input
                    type="number"
                    className="form-control"
                    name="age"
                    value={student.age}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <div className="form-group">
                <label>Grade:</label>
                <input
                    type="text"
                    className="form-control"
                    name="grade"
                    value={student.grade}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <button type="submit" className="btn btn-primary">{buttonLabel}</button>
        </form>
    );
}

export default StudentForm;
