import React, { useState, useEffect } from 'react';

const StudentForm = ({ initialStudent = {}, onSubmit }) => {
    const [student, setStudent] = useState(initialStudent);
    const defaultStudent = {
        name: '',
        age: '',
        grade: ''
    };

    useEffect(() => {
        setStudent(initialStudent);
    }, [initialStudent]);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setStudent({ ...student, [name]: value });
    };

    return (
        <form onSubmit={(event) => {
            event.preventDefault();
            onSubmit(student);
        }}>
            <div className="form-group">
                <label>Name</label>
                <input type="text" name="name" value={student.name} onChange={handleInputChange} className="form-control" required />
            </div>
            <div className="form-group">
                <label>Age</label>
                <input type="number" name="age" value={student.age} onChange={handleInputChange} className="form-control" required />
            </div>
            <div className="form-group">
                <label>Grade</label>
                <input type="text" name="grade" value={student.grade} onChange={handleInputChange} className="form-control" required />
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
        </form>
    );
}

export default StudentForm;
