import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import studentService from '../services/studentService';

function StudentView() {
    const [student, setStudent] = useState({});
    const { id } = useParams();
    const history = useHistory();

    useEffect(() => {
        const fetchStudent = async () => {
            try {
                const response = await studentService.getStudentById(id);
                setStudent(response.data);
            } catch (error) {
                console.error("There was an error fetching the student!", error);
            }
        };

        fetchStudent();
    }, [id]);

    return (
        <div className="student-view-container">
            <h2>Student Details</h2>
            <div className="student-details">
                <label><b>ID:</b> {student.id}</label><br />
                <label><b>Name:</b> {student.name}</label><br />
                <label><b>Email:</b> {student.email}</label><br />
                <label><b>Course:</b> {student.course}</label><br />
            </div>
            <button
                className="btn btn-primary mt-3"
                onClick={() => history.push(`/edit/${student.id}`)}
            >
                Edit
            </button>
        </div>
    );
}

export default StudentView;
