import React, { useState, useEffect } from 'react';
import Student from '../components/Student';
import StudentForm from '../components/StudentForm';
import StudentService from '../services/studentService';

const StudentList = () => {
    const [students, setStudents] = useState([]);
    const [currentStudent, setCurrentStudent] = useState(null);

    useEffect(() => {
        fetchStudents();
    }, []);

    const fetchStudents = async () => {
        try {
            const response = await StudentService.getAllStudents();
            setStudents(response.data);
        } catch (error) {
            console.error("Error fetching students:", error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await StudentService.deleteStudent(id);
            fetchStudents();
        } catch (error) {
            console.error("Error deleting student:", error);
        }
    };

    const handleEdit = (student) => {
        setCurrentStudent(student);
    };

    const handleSubmit = async (student) => {
        if (student.id) {
            // Update
            await StudentService.updateStudent(student.id, student);
        } else {
            // Create
            await StudentService.createStudent(student);
        }
        setCurrentStudent(null);
        fetchStudents();
    };

    const handleCancel = () => {
        setCurrentStudent(null);
    };

    return (
        <div className="container mt-4">
            <h2 className="mb-4">Student Management</h2>
            <div className="row">
                <div className="col-md-4">
                    <h4>{currentStudent ? 'Edit Student' : 'Add Student'}</h4>
                    <StudentForm initialStudent={currentStudent || {}} onSubmit={handleSubmit} onCancel={handleCancel} />
                </div>
                <div className="col-md-8">
                    <h4>Students List</h4>
                    {students.map(student => (
                        <Student key={student.id} student={student} onDelete={handleDelete} onEdit={handleEdit} />
                    ))}
                </div>
            </div>
        </div>
    );
}

export default StudentList;
