import React, { useState, useEffect } from 'react';
import Student from '../components/Student';
import StudentForm from '../components/StudentForm';
import StudentService from '../services/studentService';
import {Outlet, Link } from 'react-router-dom';

const StudentList = () => {
    const [students, setStudents] = useState([]);
    const [currentStudent, setCurrentStudent] = useState(null);

    useEffect(() => {
        fetchStudents();
    }, []);

    const fetchStudents = async () => {
        try {
            const response = await StudentService.getAllStudents();
            setStudents(response.data);
        } catch (error) {
            console.error("Error fetching students:", error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await StudentService.deleteStudent(id);
            fetchStudents();
        } catch (error) {
            console.error("Error deleting student:", error);
        }
    };

    const handleEdit = (student) => {
        setCurrentStudent(student);
    };

    const handleSubmit = async (student) => {
        if (student.id) {
            // Update
            await StudentService.updateStudent(student.id, student);
        } else {
            // Create
            await StudentService.createStudent(student);
        }
        setCurrentStudent(null);
        fetchStudents();
    };

    const handleCancel = () => {
        setCurrentStudent(null);
    };

    return (
        <div className="container mt-4">
            <h2 className="mb-4">Student Management</h2>
            <table className="table">
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Age</th>
                    <th>Grade</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {students.map(student => (
                    <tr key={student.id}>
                        <td>{student.id}</td>
                        <td>{student.name}</td>
                        <td>{student.age}</td>
                        <td>{student.grade}</td>
                        <td>
                            <Link className="btn btn-info me-2" to={`/view/${student.id}`}>View</Link>
                            <Link className="btn btn-warning me-2" to={`/edit/${student.id}`}>Edit</Link>
                            {/* Delete button functionality will be added later */}
                            <button className="btn btn-danger">Delete</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );


}

export default StudentList;
